fst' (w,x,y,z) = w
qs' [] = []
qs' (a:as) = case a of 
    (x,y,z,w) -> (let 
        pivot = a
        greaterThanPivot = [p | p <- as, fst' p > fst' a]
        lessThanPivot = [p | p <- as, fst' p <= fst' a]
        in qs' greaterThanPivot ++ [pivot] ++ qs' lessThanPivot)

tpl :: (Num a) => [a] -> [(a,a,a)]
tpl [] = [(1,1,1)]
tpl (a:b:[]) = [(1,1,1)]
tpl (a:[]) = [(1,1,1)]
tpl (a:b:c:ds) = (a,b,c) : (tpl (b:c:ds))

